/*
*
*  This component renders the team photos
*  Also adds an onclick function that changes which partner is the active partner
*  And adds an active css class for the partner if that partner is currently active
*
*/

import Link from 'next/link'

const Team = props => {
  return (
    <div className="people-list-container">
      { props.team.map(person =>
        <img
          className={props.activePartner == person.fields.name ? "img-xtra-sml padding-sml clickable person-avatar person-avatar--active" : "img-xtra-sml padding-sml clickable person-avatar"}
          onClick={() => { props.changeActivePartner(person.fields.name) }}
          key={person.fields.name}
          src={person.fields.avatar[0].thumbnails.large.url}
        />
      )}
    </div>
  )
}

export default Team
